mapper.select:
select B.BOARD_SEQ, B.TITLE, B.CONTENTS, B.REG_DATE from T_BOARD B
AND B.TITLE LIKE CONCAT('%' , '${parameter.keyword}' , '%')
AND B.BOARD_TYPE IN (
#{value}
)
ORDER BY B.REG_DATE DESC LIMIT ${pageRequest.limit}, #{pageRequest.offset}

mapper.update:
UPDATE T_BOARD
SET
	BOARD_TYPE = #{boardType.code},
	TITLE = #{title},
	CONTENTS = #{contents}
WHERE BOARD_SEQ = #{boardSeq}
